import React, { Component } from 'react';
import { Container, Row, Table} from 'react-bootstrap';

class listarUsuarios extends Component {

    constructor (props){
        super(props);
        this.state ={
            usuario :[]
        }
    }

    render() {
        return (
            <Container>
                <Row>
                    <h1>Usuarios</h1>
                </Row>
                <Row>
                    <Table striped bordered hover size="sm">
                        <thead>
                            <tr>
                                <th>
                                    Login
                                </th>
                                <th>
                                    Senha
                                </th>
                                <th>
                                    Ações
                                </th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.usuario.map(
                                    usuario =>
                                    <tr key= {usuario.id}>
                                        <td>
                                            {usuario.login}
                                        </td>
                                        <td>
                                            {usuario.senha}
                                        </td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </Table>
                </Row>
            </Container>
        );
    }
}

export default listarUsuarios;
