import React, { Component } from 'react';
import { Container, Row, Table, Button, Col} from 'react-bootstrap';
import UsuarioServices from '../services/UsuarioServices';

class listarUsuarios extends Component {

    constructor (props){
        super(props);
        this.state = {
            usuarios: []
        }

        this.voltar = this.voltar.bind(this);

    }

    componentDidMount(){
        this.getUsuarios();
    }

    getUsuarios(){
        UsuarioServices.getUsuario().then((resposta) => {
            this.setState({usuarios: resposta.data});
            console.log(resposta.data);
        });
    }

    voltar(){
        this.props.history.push("/")
    }

    render() {
        return (
            <Container>
                <Row>
                    <h1>Usuarios</h1>
                </Row>
                <Row className="float-left">
                    <Button variant="link" >voltar</Button>
                </Row>
                <Row>
                    <Table striped bordered hover size="sm">
                        <thead>
                            <tr>
                                <th>
                                    Email
                                </th>
                                <th>
                                    Senha
                                </th>
                                <th>
                                    Ações
                                </th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.usuarios.map(
                                    usuario =>
                                        <tr key = {usuario.id_usuario}>
                                            <td>
                                                {usuario.email_usuario}
                                            </td>
                                            <td>
                                                {usuario.senha_usuario}
                                            </td>
                                            <td>
                                                <Button variant="warning" onClick="" >Editar</Button>
                                                <Button variant="danger" onClick="" >Excluir</Button>
                                            </td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </Table>
                </Row>
                <Row >
                    <Col>
                        <Button className="float-left" variant="link" onClick={this.voltar}>voltar</Button>
                    </Col>
                    <Col>
                        <Button  className="float-right" variant="secondary" onClick="">Novo Usuario</Button>
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default listarUsuarios;
